import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import { fileURLToPath } from "url";

// Fix __dirname equivalent for ESM
const __dirname = path.dirname(fileURLToPath(import.meta.url));

export default defineConfig({
  plugins: [react()],
  resolve: {
    alias: {
      "@": path.resolve(__dirname, "client", "src"),
      "@shared": path.resolve(__dirname, "shared"),
      "@assets": path.resolve(__dirname, "attached_assets"),
    },
  },
  root: path.resolve(__dirname, "client"), // frontend root folder
  build: {
    outDir: path.resolve(__dirname, "dist"), // ‚úÖ final build will go into /dist
    emptyOutDir: true,
  },
  base: "./", // ‚úÖ important for correct asset paths in production
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

