        rows: [
          { Month: 'Jan', Revenue: '125000', Expenses: '85000', Profit: '40000', Growth: '12' },
          { Month: 'Feb', Revenue: '138000', Expenses: '92000', Profit: '46000', Growth: '15' },
          { Month: 'Mar', Revenue: '142000', Expenses: '89000', Profit: '53000', Growth: '18' },
