import { useState } from 'react';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Navbar } from '@/components/job-portal/navbar';
import { Plus, Building, Globe, Linkedin, MapPin } from 'lucide-react';
import type { InsertCompany, Company } from '@shared/schema';

function AddCompanyDialog({ children }: { children: React.ReactNode }) {
  const [open, setOpen] = useState(false);
  const [formData, setFormData] = useState<InsertCompany>({
    name: '',
    description: '',
    website: '',
    linkedinUrl: '',
    logo: '',
    location: '',
  });
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const createCompanyMutation = useMutation({
    mutationFn: async (data: InsertCompany) => {
      const response = await apiRequest('POST', '/api/companies', data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: 'Company added successfully',
        description: 'The company has been added to the database.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/companies'] });
      setOpen(false);
      setFormData({
        name: '',
        description: '',
        website: '',
        linkedinUrl: '',
        logo: '',
        location: '',
      });
    },
    onError: (error) => {
      toast({
        title: 'Failed to add company',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.name.trim()) {
      toast({
        title: 'Please enter company name',
        description: 'Company name is required.',
        variant: 'destructive',
      });
      return;
    }
    createCompanyMutation.mutate(formData);
  };

  const handleChange = (field: keyof InsertCompany, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value,
    }));
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {children}
      </DialogTrigger>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Add New Company</DialogTitle>
          <DialogDescription>
            Add a new company to the database for job postings.
          </DialogDescription>
        </DialogHeader>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="company-name">Company Name *</Label>
            <Input
              id="company-name"
              value={formData.name}
              onChange={(e) => handleChange('name', e.target.value)}
              placeholder="Enter company name"
              required
              data-testid="company-name-input"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="company-description">Description</Label>
            <Textarea
              id="company-description"
              value={formData.description || ''}
              onChange={(e) => handleChange('description', e.target.value)}
              placeholder="Company description"
              className="min-h-20"
              data-testid="company-description-textarea"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="company-website">Website</Label>
            <Input
              id="company-website"
              value={formData.website || ''}
              onChange={(e) => handleChange('website', e.target.value)}
              placeholder="https://company.com"
              data-testid="company-website-input"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="company-linkedin">LinkedIn URL</Label>
            <Input
              id="company-linkedin"
              value={formData.linkedinUrl || ''}
              onChange={(e) => handleChange('linkedinUrl', e.target.value)}
              placeholder="https://linkedin.com/company/..."
              data-testid="company-linkedin-input"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="company-location">Location</Label>
            <Input
              id="company-location"
              value={formData.location || ''}
              onChange={(e) => handleChange('location', e.target.value)}
              placeholder="City, Country"
              data-testid="company-location-input"
            />
          </div>

          <div className="flex justify-end space-x-2 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={() => setOpen(false)}
              data-testid="cancel-company-button"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={createCompanyMutation.isPending}
              data-testid="create-company-button"
            >
              {createCompanyMutation.isPending ? 'Adding...' : 'Add Company'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

export default function Companies() {
  const { data: companies = [], isLoading } = useQuery<Company[]>({
    queryKey: ['/api/companies'],
  });

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6 md:py-8">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-6 md:mb-8 gap-4">
          <div>
            <h1 className="text-2xl md:text-3xl font-bold text-gray-900 mb-2">Companies</h1>
            <p className="text-gray-600">
              Manage companies for job postings and recruitment.
            </p>
          </div>

          <AddCompanyDialog>
            <Button className="w-full sm:w-auto" data-testid="add-company-button">
              <Plus className="w-4 h-4 mr-2" />
              Add Company
            </Button>
          </AddCompanyDialog>
        </div>

        {isLoading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6">
            {Array.from({ length: 6 }).map((_, i) => (
              <Card key={i} className="animate-pulse">
                <CardContent className="p-4 md:p-6">
                  <div className="h-6 bg-gray-200 rounded mb-2"></div>
                  <div className="h-4 bg-gray-200 rounded mb-4"></div>
                  <div className="space-y-2">
                    <div className="h-3 bg-gray-200 rounded"></div>
                    <div className="h-3 bg-gray-200 rounded w-2/3"></div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6">
            {companies.map((company) => (
              <Card key={company.id} className="hover:shadow-lg transition-shadow">
                <CardHeader className="pb-3">
                  <div className="flex items-start justify-between">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 md:w-12 md:h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                        <Building className="w-5 h-5 md:w-6 md:h-6 text-blue-600" />
                      </div>
                      <div>
                        <CardTitle className="text-base md:text-lg">{company.name}</CardTitle>
                        {company.location && (
                          <div className="flex items-center text-sm text-gray-500 mt-1">
                            <MapPin className="w-3 h-3 mr-1" />
                            {company.location}
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                </CardHeader>

                <CardContent className="pt-0">
                  {company.description && (
                    <CardDescription className="text-sm mb-4 line-clamp-3">
                      {company.description}
                    </CardDescription>
                  )}

                  <div className="flex flex-wrap gap-2">
                    {company.website && (
                      <Button
                        variant="outline"
                        size="sm"
                        asChild
                        className="text-xs"
                        data-testid={`company-website-${company.id}`}
                      >
                        <a href={company.website} target="_blank" rel="noopener noreferrer">
                          <Globe className="w-3 h-3 mr-1" />
                          Website
                        </a>
                      </Button>
                    )}

                    {company.linkedinUrl && (
                      <Button
                        variant="outline"
                        size="sm"
                        asChild
                        className="text-xs"
                        data-testid={`company-linkedin-${company.id}`}
                      >
                        <a href={company.linkedinUrl} target="_blank" rel="noopener noreferrer">
                          <Linkedin className="w-3 h-3 mr-1" />
                          LinkedIn
                        </a>
                      </Button>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {!isLoading && companies.length === 0 && (
          <div className="text-center py-12">
            <Building className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No companies yet</h3>
            <p className="text-gray-500 mb-4">
              Start by adding your first company to the database.
            </p>
            <AddCompanyDialog>
              <Button data-testid="add-first-company-button">
                <Plus className="w-4 h-4 mr-2" />
                Add First Company
              </Button>
            </AddCompanyDialog>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/course-details.tsx.content:
import { useState, useEffect } from 'react';
import { useLocation, useRoute } from 'wouter';
import { useQuery } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Separator } from '@/components/ui/separator';
import { Navbar } from '@/components/job-portal/navbar';
import { 
  ArrowLeft, 
  Play, 
  Clock, 
  User, 
  Star,
  BookOpen,
  CheckCircle,
  Download,
  Users,
  Award,
  Target
} from 'lucide-react';
import type { Course } from '@shared/schema';

export default function CourseDetails() {
  const [, navigate] = useLocation();
  const [, params] = useRoute('/courses/:id');
  const [isEnrolled, setIsEnrolled] = useState(false);
  const [progress, setProgress] = useState(0);

  // Check if user is logged in
  useEffect(() => {
    const user = localStorage.getItem('user');
    if (!user) {
      navigate('/login');
    }
  }, [navigate]);

  const { data: course, isLoading } = useQuery<Course>({
    queryKey: ['/api/courses', params?.id],
    enabled: !!params?.id,
  });

  // Mock course curriculum data
  const curriculum = [
    {
      title: 'Introduction and Setup',
      duration: '45 min',
      lessons: [
        'Course Overview and Goals',
        'Setting up Development Environment',
        'Understanding Basic Concepts',
        'Your First Project Setup'
      ],
      completed: true
    },
    {
      title: 'Fundamentals',
      duration: '2 hours',
      lessons: [
        'Core Concepts and Syntax',
        'Variables and Data Types',
        'Control Structures',
        'Functions and Methods',
        'Practice Exercises'
      ],
      completed: true
    },
    {
      title: 'Intermediate Topics',
      duration: '3 hours',
      lessons: [
        'Object-Oriented Programming',
        'Error Handling',
        'File Operations',
        'Working with APIs',
        'Building Real Projects'
      ],
      completed: false
    },
    {
      title: 'Advanced Concepts',
      duration: '2.5 hours',
      lessons: [
        'Advanced Patterns',
        'Performance Optimization',
        'Testing and Debugging',
        'Deployment Strategies'
      ],
      completed: false
    },
    {
      title: 'Final Project',
      duration: '4 hours',
      lessons: [
        'Project Planning',
        'Implementation Phase',
        'Code Review and Optimization',
        'Deployment and Documentation'
      ],
      completed: false
    }
  ];

  const completedSections = curriculum.filter(section => section.completed).length;
  const progressPercentage = (completedSections / curriculum.length) * 100;

  useEffect(() => {
    setProgress(progressPercentage);
    setIsEnrolled(progressPercentage > 0);
  }, [progressPercentage]);

  const handleEnroll = () => {
    setIsEnrolled(true);
    setProgress(20); // Start with some progress
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Navbar />
        <div className="flex items-center justify-center h-96">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-600">Loading course details...</p>
          </div>
        </div>
      </div>
    );
  }

  if (!course) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Navbar />
        <div className="max-w-4xl mx-auto px-4 py-8">
          <Card>
            <CardContent className="p-8 text-center">
              <h3 className="text-lg font-medium text-gray-900 mb-2">Course not found</h3>
              <p className="text-gray-600 mb-4">The course you're looking for doesn't exist or has been removed.</p>
              <Button onClick={() => navigate('/courses')}>
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Courses
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  const getLevelColor = (level: string) => {
    switch (level) {
      case 'beginner': return 'bg-green-100 text-green-800';
      case 'intermediate': return 'bg-yellow-100 text-yellow-800';
      case 'advanced': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Back Button */}
        <Button 
          variant="ghost" 
          onClick={() => navigate('/courses')}
          className="mb-6"
          data-testid="back-to-courses"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Courses
        </Button>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-6">
            {/* Course Header */}
            <Card>
              <div className="h-64 bg-gradient-to-r from-blue-500 to-purple-600 rounded-t-lg flex items-center justify-center">
                <Play className="w-20 h-20 text-white" />
              </div>
              <CardHeader>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <CardTitle className="text-2xl lg:text-3xl mb-4">{course.title}</CardTitle>
                    <CardDescription className="text-lg mb-4">
                      {course.description}
                    </CardDescription>

                    <div className="flex flex-wrap items-center gap-4 mb-4">
                      <Badge className={getLevelColor(course.level)}>
                        {course.level}
                      </Badge>
                      {course.instructor && (
                        <div className="flex items-center">
                          <User className="w-4 h-4 mr-2 text-gray-500" />
                          <span className="text-sm text-gray-600">{course.instructor}</span>
                        </div>
                      )}
                      {course.duration && (
                        <div className="flex items-center">
                          <Clock className="w-4 h-4 mr-2 text-gray-500" />
                          <span className="text-sm text-gray-600">{course.duration}</span>
                        </div>
                      )}
                    </div>

                    <div className="flex items-center mb-4">
                      <div className="flex items-center">
                        {[1, 2, 3, 4, 5].map((star) => (
                          <Star 
                            key={star} 
                            className="w-4 h-4 fill-yellow-400 text-yellow-400" 
                          />
                        ))}
                      </div>
                      <span className="text-sm text-gray-600 ml-2">(4.8) ‚Ä¢ 2,450 students</span>
                    </div>
                  </div>
                </div>
              </CardHeader>
            </Card>

            {/* Course Progress (if enrolled) */}
            {isEnrolled && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <Target className="w-5 h-5 mr-2" />
                    Your Progress
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex items-center justify-between">
                      <span className="text-sm font-medium">Course Completion</span>
                      <span className="text-sm text-gray-600">{Math.round(progress)}%</span>
                    </div>
                    <Progress value={progress} className="w-full" />
                    <p className="text-sm text-gray-600">
                      {completedSections} of {curriculum.length} sections completed
                    </p>
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Course Curriculum */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <BookOpen className="w-5 h-5 mr-2" />
                  Course Curriculum
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {curriculum.map((section, index) => (
                    <div key={index} className="border rounded-lg p-4">
                      <div className="flex items-center justify-between mb-3">
                        <div className="flex items-center">
                          {section.completed ? (
                            <CheckCircle className="w-5 h-5 text-green-600 mr-3" />
                          ) : (
                            <div className="w-5 h-5 border-2 border-gray-300 rounded-full mr-3" />
                          )}
                          <h4 className="font-medium text-gray-900">{section.title}</h4>
                        </div>
                        <div className="flex items-center text-sm text-gray-600">
                          <Clock className="w-4 h-4 mr-1" />
                          {section.duration}
                        </div>
                      </div>

                      <div className="ml-8 space-y-2">
                        {section.lessons.map((lesson, lessonIndex) => (
                          <div key={lessonIndex} className="flex items-center text-sm text-gray-600">
                            <div className="w-2 h-2 bg-gray-300 rounded-full mr-3" />
                            {lesson}
                          </div>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* What You'll Learn */}
            <Card>
              <CardHeader>
                <CardTitle>What You'll Learn</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid md:grid-cols-2 gap-4">
                  {[
                    'Master the fundamentals and best practices',
                    'Build real-world projects from scratch',
                    'Understand industry-standard workflows',
                    'Learn debugging and troubleshooting',
                    'Prepare for technical interviews',
                    'Get hands-on experience with tools'
                  ].map((item, index) => (
                    <div key={index} className="flex items-start">
                      <CheckCircle className="w-4 h-4 text-green-600 mt-0.5 mr-3 flex-shrink-0" />
                      <span className="text-sm text-gray-700">{item}</span>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Enrollment Card */}
            <Card>
              <CardHeader>
                <CardTitle>Course Access</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="text-center">
                  <div className="text-3xl font-bold text-green-600 mb-2">
                    {course.price}
                  </div>
                </div>

                {isEnrolled ? (
                  <div className="space-y-3">
                    <Button className="w-full" size="lg" data-testid="continue-learning">
                      <Play className="w-4 h-4 mr-2" />
                      Continue Learning
                    </Button>
                    <Button variant="outline" className="w-full" data-testid="download-materials">
                      <Download className="w-4 h-4 mr-2" />
                      Download Materials
                    </Button>
                  </div>
                ) : (
                  <Button 
                    className="w-full" 
                    size="lg"
                    onClick={handleEnroll}
                    data-testid="enroll-course"
                  >
                    {course.price === 'Free' ? 'Enroll Now' : 'Enroll Now'}
                  </Button>
                )}

                <div className="text-center">
                  <p className="text-xs text-gray-600">
                    30-day money-back guarantee
                  </p>
                </div>
              </CardContent>
            </Card>

            {/* Course Info */}
            <Card>
              <CardHeader>
                <CardTitle>Course Information</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Skill Level:</span>
                  <Badge className={getLevelColor(course.level)}>
                    {course.level}
                  </Badge>
                </div>

                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Students:</span>
                  <span className="text-sm font-medium">2,450</span>
                </div>

                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Languages:</span>
                  <span className="text-sm font-medium">English</span>
                </div>

                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Includes:</span>
                  <span className="text-sm font-medium">Certificate</span>
                </div>

                <Separator />

                <div className="space-y-3">
                  <div className="flex items-center text-sm">
                    <Users className="w-4 h-4 mr-2 text-blue-600" />
                    <span>Lifetime access</span>
                  </div>
                  <div className="flex items-center text-sm">
                    <Award className="w-4 h-4 mr-2 text-yellow-600" />
                    <span>Certificate of completion</span>
                  </div>
                  <div className="flex items-center text-sm">
                    <Download className="w-4 h-4 mr-2 text-green-600" />
                    <span>Downloadable resources</span>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Instructor */}
            {course.instructor && (
              <Card>
                <CardHeader>
                  <CardTitle>Instructor</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="flex items-center mb-4">
                    <div className="w-12 h-12 bg-blue-600 rounded-full flex items-center justify-center mr-4">
                      <User className="w-6 h-6 text-white" />
                    </div>
                    <div>
                      <h4 className="font-medium">{course.instructor}</h4>
                      <p className="text-sm text-gray-600">Senior Developer & Instructor</p>
                    </div>
                  </div>

                  <div className="flex items-center mb-3">
                    <div className="flex items-center">
                      {[1, 2, 3, 4, 5].map((star) => (
                        <Star 
                          key={star} 
                          className="w-4 h-4 fill-yellow-400 text-yellow-400" 
                        />
                      ))}
                    </div>
                    <span className="text-sm text-gray-600 ml-2">(4.9)</span>
                  </div>

                  <p className="text-sm text-gray-700">
                    Experienced developer with 8+ years in the industry. Has taught over 50,000 students worldwide.
                  </p>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

