      technologies: ['React', 'JavaScript', 'CSS', 'Local Storage'],
      githubUrl: 'https://github.com/microsoft/TodoMVC',
      liveUrl: 'https://todomvc.com/examples/react/',
      duration: '2-3 hours',
      featured: true,
    },
    {
      id: 'weather-app',
      title: 'Weather Dashboard',
