import { useState, useEffect } from 'react';
import { useLocation } from 'wouter';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Navbar } from '@/components/job-portal/navbar';
import { 
  Briefcase, 
  MapPin, 
  Clock, 
  Building, 
  Trash2,
  ExternalLink,
  Calendar,
  CheckCircle
} from 'lucide-react';

export default function MyApplications() {
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [user, setUser] = useState<any>({});

  // Check if user is logged in
  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (!userData) {
      navigate('/login');
      return;
    }
    setUser(JSON.parse(userData));
  }, [navigate]);

  const { data: applications = [], isLoading } = useQuery({
    queryKey: ['/api/applications/user', user.id],
    enabled: !!user.id,
  });

  const { data: jobs = [] } = useQuery({
    queryKey: ['/api/jobs'],
  });

  const removeApplicationMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      const response = await apiRequest('DELETE', `/api/applications/${applicationId}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/applications/user', user.id] });
      toast({
        title: 'Application removed',
        description: 'Your job application has been removed successfully.',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Remove failed',
        description: error.message || 'Failed to remove application',
        variant: 'destructive',
      });
    },
  });

  // Get job details for each application
  const applicationsWithJobs = Array.isArray(applications) ? applications.map((app: any) => {
    const job = Array.isArray(jobs) ? jobs.find((j: any) => j.id === app.jobId) : null;
    return { ...app, job };
  }).filter(app => app.job) : [];

  const handleRemoveApplication = (applicationId: string) => {
    if (window.confirm('Are you sure you want to remove this application?')) {
      removeApplicationMutation.mutate(applicationId);
    }
  };

  const handleViewJob = (jobId: string) => {
    navigate(`/jobs/${jobId}`);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Navbar />
        <div className="flex items-center justify-center h-96">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-600">Loading your applications...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />

      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">My Applications</h1>
              <p className="text-gray-600 mt-2">Track and manage your job applications</p>
            </div>
            <div className="text-right">
              <div className="text-3xl font-bold text-blue-600">{applicationsWithJobs.length}</div>
              <div className="text-sm text-gray-600">Total Applications</div>
            </div>
          </div>
        </div>

        {/* Applications List */}
        <div className="space-y-6">
          {applicationsWithJobs.length === 0 ? (
            <Card>
              <CardContent className="p-12 text-center">
                <div className="text-gray-400 mb-4">
                  <Briefcase className="w-16 h-16 mx-auto" />
                </div>
                <h3 className="text-xl font-medium text-gray-900 mb-2">No Applications Yet</h3>
                <p className="text-gray-600 mb-6">
                  You haven't applied to any jobs yet. Start exploring opportunities and apply to jobs that match your skills.
                </p>
                <Button onClick={() => navigate('/jobs')} data-testid="browse-jobs-button">
                  <Briefcase className="w-4 h-4 mr-2" />
                  Browse Jobs
                </Button>
              </CardContent>
            </Card>
          ) : (
            applicationsWithJobs.map((application: any) => {
              const { job } = application;
              const appliedDate = new Date(application.createdAt || application.appliedAt);
              const isExpired = new Date(job.closingDate) < new Date();

              return (
                <Card 
                  key={application.id} 
                  className="hover:shadow-md transition-shadow"
                  data-testid={`application-card-${application.id}`}
                >
                  <CardHeader>
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center space-x-3 mb-2">
                          <CardTitle className="text-xl">{job.title}</CardTitle>
                          <Badge className="bg-green-500 text-white">
                            <CheckCircle className="w-3 h-3 mr-1" />
                            Applied
                          </Badge>
                          {isExpired && (
                            <Badge variant="destructive">Expired</Badge>
                          )}
                        </div>

                        <div className="flex items-center space-x-6 text-gray-600 mb-3">
                          <div className="flex items-center">
                            <Building className="w-4 h-4 mr-2" />
                            <span className="font-medium">{job.company.name}</span>
                          </div>
                          <div className="flex items-center">
                            <MapPin className="w-4 h-4 mr-2" />
                            <span>{job.location}</span>
                          </div>
                          <div className="flex items-center">
                            <Calendar className="w-4 h-4 mr-2" />
                            <span>Applied: {appliedDate.toLocaleDateString()}</span>
                          </div>
                        </div>

                        <div className="flex items-center space-x-4 text-sm text-gray-600 mb-4">
                          <span className="flex items-center">
                            <span className="font-medium text-green-600">{job.salary}</span>
                          </span>
                          <span className="flex items-center">
                            <Clock className="w-4 h-4 mr-1" />
                            Closes: {new Date(job.closingDate).toLocaleDateString()}
                          </span>
                        </div>
                      </div>

                      {/* Company Logo */}
                      <div className="ml-6">
                        <div className="w-12 h-12 bg-gray-100 rounded-lg flex items-center justify-center">
                          <img 
                            src={job.company.logo || '/api/placeholder/48/48'} 
                            alt={job.company.name}
                            className="w-10 h-10 object-contain"
                            onError={(e) => {
                              const target = e.target as HTMLImageElement;
                              target.style.display = 'none';
                              target.nextElementSibling!.textContent = job.company.name.charAt(0).toUpperCase();
                            }}
                          />
                          <span className="hidden text-lg font-semibold text-gray-600"></span>
                        </div>
                      </div>
                    </div>
                  </CardHeader>

                  <CardContent>
                    <CardDescription className="mb-4">
                      {job.description.substring(0, 200)}...
                    </CardDescription>

                    {/* Skills */}
                    <div className="flex flex-wrap gap-2 mb-4">
                      {job.skills.split(',').slice(0, 5).map((skill: string, index: number) => (
                        <Badge key={index} variant="outline" className="text-xs">
                          {skill.trim()}
                        </Badge>
                      ))}
                    </div>

                    <Separator className="my-4" />

                    {/* Actions */}
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <span className="text-sm text-gray-600">
                          Application Status: <span className="text-green-600 font-medium">Submitted</span>
                        </span>
                      </div>

                      <div className="flex items-center space-x-3">
                        <Button 
                          variant="outline" 
                          size="sm"
                          onClick={() => handleViewJob(job.id)}
                          data-testid={`view-job-${application.id}`}
                        >
                          <ExternalLink className="w-4 h-4 mr-2" />
                          View Job
                        </Button>
                        <Button 
                          variant="destructive" 
                          size="sm"
                          onClick={() => handleRemoveApplication(application.id)}
                          disabled={removeApplicationMutation.isPending}
                          data-testid={`remove-application-${application.id}`}
                        >
                          <Trash2 className="w-4 h-4 mr-2" />
                          Remove
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
}

client/src/pages/profile.tsx.content:
import { useState, useEffect } from 'react';
import { useLocation } from 'wouter';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Navbar } from '@/components/job-portal/navbar';
import { 
  User, 
  Phone, 
  Mail, 
  Lock, 
  Edit,
  Save,
  X
} from 'lucide-react';

export default function Profile() {
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [user, setUser] = useState<any>({});
  const [editMode, setEditMode] = useState({
    name: false,
    phone: false,
    email: false,
    password: false
  });

  const [formData, setFormData] = useState({
    fullName: '',
    phone: '',
    email: '',
    currentPassword: '',
    newPassword: ''
  });

  // Check if user is logged in and load data
  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (!userData) {
      navigate('/login');
      return;
    }
    const parsedUser = JSON.parse(userData);
    setUser(parsedUser);
    setFormData({
      fullName: parsedUser.fullName || '',
      phone: parsedUser.phone || '',
      email: parsedUser.email || '',
      currentPassword: '',
      newPassword: ''
    });
  }, [navigate]);

  const updateProfileMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest('PATCH', `/api/users/${user.id}`, data);
      return response.json();
    },
    onSuccess: (updatedUser) => {
      // Update localStorage
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);

      // Close edit mode
      setEditMode({
        name: false,
        phone: false,
        email: false,
        password: false
      });

      toast({
        title: 'Profile updated',
        description: 'Your profile has been updated successfully.',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Update failed',
        description: error.message || 'Failed to update profile',
        variant: 'destructive',
      });
    },
  });

  const handleEdit = (field: string) => {
    setEditMode(prev => ({ ...prev, [field]: true }));
  };

  const handleCancel = (field: string) => {
    setEditMode(prev => ({ ...prev, [field]: false }));
    // Reset form data
    setFormData(prev => ({
      ...prev,
      fullName: user.fullName || '',
      phone: user.phone || '',
      email: user.email || '',
      currentPassword: '',
      newPassword: ''
    }));
  };

  const handleSave = (field: string) => {
    let updateData: any = {};

    switch (field) {
      case 'name':
        updateData = { fullName: formData.fullName };
        break;
      case 'phone':
        updateData = { phone: formData.phone };
        break;
      case 'email':
        updateData = { email: formData.email };
        break;
      case 'password':
        if (!formData.currentPassword || !formData.newPassword) {
          toast({
            title: 'Password update failed',
            description: 'Please provide both current and new password',
            variant: 'destructive',
          });
          return;
        }
        updateData = { 
          currentPassword: formData.currentPassword,
          newPassword: formData.newPassword 
        };
        break;
    }

    updateProfileMutation.mutate(updateData);
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />

      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Account Profile</h1>
          <p className="text-gray-600 mt-2">Manage your account details and preferences</p>
        </div>

        <div className="grid gap-6">
          {/* Personal Information */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <User className="w-5 h-5 mr-2" />
                Personal Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Full Name */}
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <Label htmlFor="fullName">Full Name</Label>
                  {editMode.name ? (
                    <div className="flex items-center space-x-2 mt-2">
                      <Input
                        id="fullName"
                        value={formData.fullName}
                        onChange={(e) => handleInputChange('fullName', e.target.value)}
                        className="flex-1"
                        data-testid="edit-name-input"
                      />
                      <Button 
                        size="sm" 
                        onClick={() => handleSave('name')}
                        disabled={updateProfileMutation.isPending}
                        data-testid="save-name-button"
                      >
                        <Save className="w-4 h-4" />
                      </Button>
                      <Button 
                        size="sm" 
                        variant="outline" 
                        onClick={() => handleCancel('name')}
                        data-testid="cancel-name-button"
                      >
                        <X className="w-4 h-4" />
                      </Button>
                    </div>
                  ) : (
                    <div className="flex items-center justify-between mt-2">
                      <p className="text-gray-900 font-medium">{user.fullName || 'Not provided'}</p>
                      <Button 
                        size="sm" 
                        variant="ghost" 
                        onClick={() => handleEdit('name')}
                        data-testid="edit-name-button"
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                    </div>
                  )}
                </div>
              </div>

              {/* Phone */}
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <Label htmlFor="phone">Phone Number</Label>
                  {editMode.phone ? (
                    <div className="flex items-center space-x-2 mt-2">
                      <Input
                        id="phone"
                        value={formData.phone}
                        onChange={(e) => handleInputChange('phone', e.target.value)}
                        className="flex-1"
                        data-testid="edit-phone-input"
                      />
                      <Button 
                        size="sm" 
                        onClick={() => handleSave('phone')}
                        disabled={updateProfileMutation.isPending}
                        data-testid="save-phone-button"
                      >
                        <Save className="w-4 h-4" />
                      </Button>
                      <Button 
                        size="sm" 
                        variant="outline" 
                        onClick={() => handleCancel('phone')}
                        data-testid="cancel-phone-button"
                      >
                        <X className="w-4 h-4" />
                      </Button>
                    </div>
                  ) : (
                    <div className="flex items-center justify-between mt-2">
                      <div className="flex items-center">
                        <Phone className="w-4 h-4 mr-2 text-gray-500" />
                        <p className="text-gray-900 font-medium">{user.phone || 'Not provided'}</p>
                      </div>
                      <Button 
                        size="sm" 
                        variant="ghost" 
                        onClick={() => handleEdit('phone')}
                        data-testid="edit-phone-button"
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                    </div>
                  )}
                </div>
              </div>

              {/* Email */}
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <Label htmlFor="email">Email Address</Label>
                  {editMode.email ? (
                    <div className="flex items-center space-x-2 mt-2">
                      <Input
                        id="email"
                        type="email"
                        value={formData.email}
                        onChange={(e) => handleInputChange('email', e.target.value)}
                        className="flex-1"
                        data-testid="edit-email-input"
                      />
                      <Button 
                        size="sm" 
                        onClick={() => handleSave('email')}
                        disabled={updateProfileMutation.isPending}
                        data-testid="save-email-button"
                      >
                        <Save className="w-4 h-4" />
                      </Button>
                      <Button 
                        size="sm" 
                        variant="outline" 
                        onClick={() => handleCancel('email')}
                        data-testid="cancel-email-button"
                      >
                        <X className="w-4 h-4" />
                      </Button>
                    </div>
                  ) : (
                    <div className="flex items-center justify-between mt-2">
                      <div className="flex items-center">
                        <Mail className="w-4 h-4 mr-2 text-gray-500" />
                        <p className="text-gray-900 font-medium">{user.email}</p>
                      </div>
                      <Button 
                        size="sm" 
                        variant="ghost" 
                        onClick={() => handleEdit('email')}
                        data-testid="edit-email-button"
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                    </div>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Security */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Lock className="w-5 h-5 mr-2" />
                Security
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <Label>Password</Label>
                  {editMode.password ? (
                    <div className="space-y-3 mt-2">
                      <Input
                        type="password"
                        placeholder="Current Password"
                        value={formData.currentPassword}
                        onChange={(e) => handleInputChange('currentPassword', e.target.value)}
                        data-testid="current-password-input"
                      />
                      <Input
                        type="password"
                        placeholder="New Password"
                        value={formData.newPassword}
                        onChange={(e) => handleInputChange('newPassword', e.target.value)}
                        data-testid="new-password-input"
                      />
                      <div className="flex items-center space-x-2">
                        <Button 
                          size="sm" 
                          onClick={() => handleSave('password')}
                          disabled={updateProfileMutation.isPending}
                          data-testid="save-password-button"
                        >
                          <Save className="w-4 h-4 mr-2" />
                          Update Password
                        </Button>
                        <Button 
                          size="sm" 
                          variant="outline" 
                          onClick={() => handleCancel('password')}
                          data-testid="cancel-password-button"
                        >
                          <X className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  ) : (
                    <div className="flex items-center justify-between mt-2">
                      <p className="text-gray-600">‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢</p>
                      <Button 
                        size="sm" 
                        variant="ghost" 
                        onClick={() => handleEdit('password')}
                        data-testid="edit-password-button"
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                    </div>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

